(ns day1.core
  (:gen-class))

(defn santas-floor [s]
  (let [f (frequencies s)]
    (- (get f \( 0) (get f \) 0))))

(defn func [[s k] v]
  (let [x (case v
            \( 1
            \) -1)
        n (+ s x)]
      (when (= n -1) (println (inc k)))
      [n (inc k)]))

(defn floor-minus-one [s]
  (reduce func [0 0] s))

